import { useRef, useState } from 'react';
import { connect } from 'react-redux';
import { addTodos, removeTodos, updateTodos } from '../../redux/reducer';
import './index.css';

const mapStateToProps = (state) => {
    return {
        todos: state,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addTodo: (obj) => dispatch(addTodos(obj)),
        removeTodo: (id) => dispatch(removeTodos(id)),
        updateTodo: (obj) => dispatch(updateTodos(obj)),
    }
}

const Todos = (props) => {

    const [todo, setTodo] = useState("");

    const inputRef = useRef(true);

    const changeFocus = () => {
        inputRef.current.disabled = false;
        inputRef.current.focus();
        // console.log("inputRef", inputRef.current.disabled);
        // console.log("inputRef focus", inputRef.current.focus);
    }

    const update = (id, value, e) => {
        console.log("e", e);
        if (e.which === 49) {
            props.updateTodo({ id, item: value });
            inputRef.current.disabled = true;
        }
    }

    const handleChange = (e) => {
        setTodo(e.target.value);
    }
    console.log("props from store", props);

    return (
        <div className="addTodos">
            <input
                type="text"
                onChange={(e) => handleChange(e)}
                className="todo-input"
            />

            <button
                className="add-btn"
                onClick={() => props.addTodo({
                    id: Math.floor(Math.random() * 1000),
                    item: todo,
                    complited: false,
                })}
            >
                Add
            </button>

            <br />

            <ul>
                {props.todos.map((item) => {
                    return (
                        <li key={item.id}>
                            <textarea
                                ref={inputRef}
                                disabled={inputRef}
                                defaultValue={item.item}
                                onKeyPress={(e) => update(item.id, inputRef.current.value, e)}
                            />

                            <button
                                onClick={() => changeFocus()}
                            >
                                Edit
                            </button>

                            <button
                                onClick={() => props.removeTodo(item.id)}
                            >
                                Delete
                            </button>{" "}

                        </li>
                    )
                })}
            </ul>
        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(Todos);
